CREATE TABLE `home` (
  `id` int(11) NOT NULL,
  `index1` longtext NOT NULL,
  `index2` longtext NOT NULL,
  `index3` longtext NOT NULL,
  `index4` longtext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

--
-- Dumping data for table `home`
